query GetPostById($id: ID!) {
  post(id: $id) {
    id
    author
    cid
  }
}

query GetPostsByAuthor($author: Bytes!) {
  posts(
    orderBy: blockTimestamp
    orderDirection: desc
    first: 6
    where: {author: $author, isDeleted: false}
  ) {
    id
    author
    cid
  }
}

query GetPosts {
  posts(
    orderBy: blockTimestamp
    orderDirection: desc
    first: 6
    where: {isDeleted: false}
  ) {
    id
    author
    cid
  }
}
